mapper.select:
SELECT ID, name, furigana, nickname, email_address, area, age, gender, remark, is_deleted FROM students
AND is_deleted = false
AND name LIKE CONCAT('%', #{name}, '%')
AND furigana LIKE CONCAT('%', #{furigana}, '%')
AND nickname LIKE CONCAT('%', #{nickname}, '%')
AND email_address LIKE CONCAT('%', #{emailAddress}, '%')
AND age = #{age}
AND area = #{area}
AND gender = #{gender}
AND is_deleted = #{isDeleted}

mapper.update:
UPDATE students SET name = #{name}, furigana = #{furigana}, nickname = #{nickname},
email_address = #{emailAddress},
area = #{area}, age = #{age}, gender = #{gender}, remark = #{remark}, is_deleted = #{deleted}
WHERE id = #{id}
